import React, { useContext, useState } from 'react';
import { useRouter } from 'next/router';
import { Button, Card, Col, Container, Form, Row, Spinner } from 'react-bootstrap';
import { toast, ToastContainer } from 'react-toastify';
import useFirebase from '../components/useFirebase';
import styles from '../styles/cardHeader.module.scss';

export default function Home(): JSX.Element {
	const authentification = useContext(useFirebase);
	const app = authentification.auth();
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [loading, setLoading] = useState(false);

	const router = useRouter();

	const handleSubmit = async () => {
		if (email.length < 8) return toast.error('please enter a valid email, must be min 8 xters...');
		if (password.length < 5) return toast.error('please enter a valid password');
		try {
			setLoading(true);
			await app.signInWithEmailAndPassword(email, password);
			toast.success('Signed in successfully');
			setLoading(false);
			router.push('/welcome');
		} catch (error) {
			return toast.error('something went wrong');
		}
	};
	return (
		<div>
			<Container>
				<Row className="justify-content-center">
					<Col md={8} sm={10} xs={12} lg={4}>
						<Card className="shadow mt-6">
							<Card.Header className={styles.header}>
								<h5 style={{ color: '#fff' }}>LOGIN</h5>
							</Card.Header>
							<Card.Body>
								<Form>
									<Form.Group>
										<Form.Label>EMAIL</Form.Label>
										<span></span>
										<Form.Control
											type="email"
											value={email}
											onChange={(e) => setEmail(e.target.value)}
											placeholder="email..."
										></Form.Control>
									</Form.Group>
									<Form.Group>
										<Form.Label>PASSWORD</Form.Label>
										<span></span>
										<Form.Control
											type="password"
											value={password}
											onChange={(e) => setPassword(e.target.value)}
											placeholder="password..."
										></Form.Control>
									</Form.Group>
									<Form.Group>
										<Button type="submit" variant="primary" onClick={handleSubmit}>
											{loading ? <Spinner animation="grow"></Spinner> : <span>Login</span>}
										</Button>
									</Form.Group>
								</Form>
							</Card.Body>
						</Card>
					</Col>
				</Row>
			</Container>
			<ToastContainer autoClose={4000} hideProgressBar={false}></ToastContainer>
		</div>
	);
}
