import React, { useContext, useState, useRef, useEffect } from 'react';
import { useRouter } from 'next/router';
import { Button, Card, Col, Container, Form, Row, Spinner } from 'react-bootstrap';
import { toast, ToastContainer } from 'react-toastify';
import useFirebase from '../components/useFirebase';
import styles from '../styles/cardHeader.module.scss';

export default function Home(): JSX.Element {
	const authentification = useContext(useFirebase);
	const app = authentification.auth();
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [loading, setLoading] = useState(false);
	const router = useRouter();
	const inputRef = useRef(null);

	useEffect(() => {
		//focus the input element on pageLoad
		inputRef.current.focus();
	}, []);

	const handleSubmit = async () => {
		if (email.length < 8) return toast.error('please enter a valid email, must be min 8 xters...');
		if (password.length < 5) return toast.error('please enter a valid password');
		setLoading(true);
		try {
			const res = await app.signInWithEmailAndPassword(email, password);
			toast.success('Signed in successfully');

			router.push('/welcome');
		} catch (error) {
			return toast.error('failed to login');
			setLoading(false);
		}
		setLoading(false);
	};
	return (
		<div className={styles.div}>
			<Container>
				<Row className="justify-content-center">
					<Col lg={6} className="mt-5">
						<Card className="shadow mt-9">
							<Card.Header className={styles.header}>
								<h5 style={{ color: '#fff' }}>LOGIN</h5>
							</Card.Header>
							<Card.Body>
								<Form>
									<Form.Group>
										<Form.Label>EMAIL</Form.Label>
										<Form.Control
											type="email"
											ref={inputRef}
											on
											value={email}
											onChange={(e) => setEmail(e.target.value)}
											placeholder="email..."
										></Form.Control>
									</Form.Group>
									<Form.Group>
										<Form.Label>PASSWORD</Form.Label>
										<span></span>
										<Form.Control
											type="password"
											value={password}
											onChange={(e) => setPassword(e.target.value)}
											placeholder="password..."
										></Form.Control>
									</Form.Group>
									<Form.Group>
										<Button variant="primary" onClick={handleSubmit}>
											{loading ? <Spinner animation="grow"></Spinner> : <span>Login</span>}
										</Button>
										<Button
											variant="secondary"
											className="p-2"
											type="button"
											onClick={() => {
												router.push('/register');
											}}
										>
											Register
										</Button>
									</Form.Group>
								</Form>
							</Card.Body>
						</Card>
					</Col>
				</Row>
			</Container>
			<ToastContainer
				autoClose={4000}
				hideProgressBar={false}
				closeButton
				closeOnClick={true}
				enableMultiContainer
			></ToastContainer>
		</div>
	);
}
