import React, { useContext, useState } from 'react';
import { useRouter } from 'next/router';
import { Button, Card, Col, Container, Form, Row, Spinner } from 'react-bootstrap';
import { toast, ToastContainer } from 'react-toastify';
import useFirebase from '../components/useFirebase';

export default function Home(): JSX.Element {
	const authentification = useContext(useFirebase);
	const app = authentification.auth();
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [loading, setLoading] = useState(false);

	const router = useRouter();

	const handleSubmit = () => {
		if (email.length < 8) return toast.error('please enter a valid email, must be min 8 xters...');
		if (password.length < 5) return toast.error('please enter a valid password');
		const res = app
			.signInWithEmailAndPassword(email, password)
			.then(() => {
				setLoading(true);

				toast.success('Signed successfully');
				router.push('/welcomePage');
				setLoading(false);
			})
			.catch((err) => {
				return toast.error('Failed to Login');
			});
	};
	return (
		<div>
			<Container>
				<Row className="justify-content-center">
					<Col md={8} sm={12} lg={4}>
						<Card className="shadow mt-6">
							<Card.Header className="text-center">
								<h5>Loging</h5>
							</Card.Header>
							<Card.Body>
								<Form>
									<Form.Group>
										<Form.Label>EMAIL</Form.Label>
										<span></span>
										<Form.Control
											type="email"
											value={email}
											onChange={(e) => setEmail(e.target.value)}
											placeholder="email..."
										></Form.Control>
									</Form.Group>
									<Form.Group>
										<Form.Label>PASSWORD</Form.Label>
										<span></span>
										<Form.Control
											type="password"
											value={password}
											onChange={(e) => setPassword(e.target.value)}
											placeholder="password..."
										></Form.Control>
									</Form.Group>
									<Form.Group>
										<Button type="submit" onClick={handleSubmit}>
											{loading ? <Spinner animation="grow"></Spinner> : <span>Login</span>}
										</Button>
									</Form.Group>
								</Form>
							</Card.Body>
						</Card>
					</Col>
				</Row>
				<ToastContainer
					position="top-center"
					autoClose={5000}
					hideProgressBar={false}
					newestOnTop={false}
					closeOnClick
					rtl={false}
					pauseOnFocusLoss
					draggable
					pauseOnHover
				/>
			</Container>
		</div>
	);
}
