import React, { useState, useEffect, useContext } from 'react';
import { Accordion, Button, Card, Container, FormControl, InputGroup, Table } from 'react-bootstrap';
import useFirebase from '../components/useFirebase';
import firebase from 'firebase';
import { useRouter } from 'next/router';

interface Customers {
	id: string;
	name: string;
	email: string;
	address: string;
	phone: string;
}
[];
export default function Customers() {
	const [customers, setCustomers] = useState<Customers[]>([]);
	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [phone, setPhone] = useState('');
	const [address, setAddress] = useState('');
	const [loading, setLoading] = useState(false);
	const router = useRouter();

	const AddCustomer = () => {
		//call the backend to add the customer
	};
	const handleDelete = (id: any) => {
		//call the database to delet the customer with the id
		console.log(id);
	};
	const handleEdit = (id: any) => {
		//find the customer with the give id and edit
		console.log(id);
	};
	useEffect(() => {
		const result = async () => {
			// await db.collection('customers').get()
		};
	});
	return (
		<Container>
			<Card className="pa-2">
				<Card.Header>
					<span className="material-icons">Customers_table</span>
					<Accordion>
						<Accordion.Toggle className="float-right btn btn-info m-2" eventKey="0">
							NEW CUSTOMER
						</Accordion.Toggle>
						<Accordion.Collapse eventKey="0">
							<Card.Body>
								<InputGroup className="mb-3">
									<InputGroup.Prepend>
										<InputGroup.Text>Details</InputGroup.Text>
									</InputGroup.Prepend>
									<FormControl
										type="text"
										placeholder="name.."
										value={name}
										onChange={(e) => setName(e.target.value)}
									/>
									<FormControl
										type="email"
										placeholder="email"
										value={email}
										onChange={(e) => setEmail(e.target.value)}
									/>
									<FormControl
										type="text"
										placeholder="address"
										value={address}
										onChange={(e) => setAddress(e.target.value)}
									/>
									<FormControl
										type="text"
										placeholder="phone"
										value={phone}
										onChange={(e) => setPhone(e.target.value)}
									/>
									<Button className="ml-2" onClick={AddCustomer}>
										Add
									</Button>
								</InputGroup>
							</Card.Body>
						</Accordion.Collapse>
					</Accordion>
				</Card.Header>
				<Card.Body>
					<Table striped bordered hover>
						<thead>
							<tr>
								<th>
									<span className="material-icons">Name</span>
								</th>
								<th>
									<span className="material-icons">Email</span>
								</th>
								<th>
									<span className="material-icons">Address</span>
								</th>
								<th>
									<span className="material-icons">Phone</span>
								</th>
								<th>
									<span className="material-icons">add</span>
								</th>
								<th>
									<span className="material-icons">delete</span>
								</th>
							</tr>
						</thead>
						<tbody>
							{customers.map((customer) => (
								<tr key={customer.id}>
									<td>{customer.name}</td>

									<td>{customer.email}</td>
									<td>{customer.address}</td>
									<td>{customer.phone}</td>
									<td>
										<Button onClick={() => handleEdit(customer.id)}>Edit</Button>
									</td>
									<td>
										<Button onClick={() => handleDelete(customer.id)}>Delete</Button>
									</td>
								</tr>
							))}
						</tbody>
					</Table>
				</Card.Body>
			</Card>
		</Container>
	);
}
