import React from 'react';
import { Table, Button } from 'react-bootstrap';
export default function CustomersTable(props: any): JSX.Element {
	const { customersPaginate, onDelete, onEdit, sortColumns } = props;
	const raiseSort = (path: string) => {
		const sortColumn = { ...sortColumns };
		if (sortColumn.path === path) sortColumn.order = sortColumn.order === 'asc' ? 'desc' : 'asc';
		else {
			sortColumn.path = path;
			sortColumn.order = 'asc';
		}
		props.onSort(sortColumn);
	};
	return (
		<Table striped bordered hover>
			<thead>
				<tr>
					<th>S/N</th>
					<th onClick={() => raiseSort('name')}>
						<span className="material-icons">person</span>
					</th>
					<th onClick={() => raiseSort('email')}>
						<span className="material-icons">mail</span>
					</th>
					<th onClick={() => raiseSort('address')}>
						<span className="material-icons">address</span>
					</th>
					<th onClick={() => raiseSort('phone')}>
						<span className="material-icons">contact_phone</span>
					</th>
					<th>
						<span className="material-icons">add</span>
					</th>
					<th>
						<span className="material-icons">delete</span>
					</th>
				</tr>
			</thead>
			<tbody>
				{customersPaginate.map((customer: any, index: any) => (
					<tr key={customer.id}>
						<td>{index + 1}</td>
						<td>{customer.name}</td>

						<td>{customer.email}</td>
						<td>{customer.address}</td>
						<td>{customer.phone}</td>

						<td>
							<Button onClick={() => onEdit(customer._id)}>Edit</Button>
						</td>
						<td>
							<Button onClick={() => onDelete(customer._id)} className="btn-danger">
								Delete
							</Button>
						</td>
					</tr>
				))}
			</tbody>
		</Table>
	);
}
