import React, { useState, useEffect, useContext } from 'react';
import { Accordion, Button, Card, Container, FormControl, InputGroup, Spinner, Table } from 'react-bootstrap';
import useFirebase from '../components/useFirebase';
import { useRouter } from 'next/router';
import { toast, ToastContainer } from 'react-toastify';

export interface Customers {
	id: string;
	name: string;
	email: string;
	address: string;
	phone: string;
	type: 'Roaster' | 'One-off';
	_id?: any;
}

export default function Customers(): JSX.Element {
	const [customers, setCustomers] = useState<Array<Customers>>([]);
	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [phone, setPhone] = useState('');
	const [address, setAddress] = useState('');
	const [loading, setLoading] = useState(false);
	const router = useRouter();
	const app = useContext(useFirebase);
	const firestore = app.firestore();

	const handleDelete = async (id: string) => {
		const deleteConfirm = confirm('Are you sure you ant to delete this document?');
		if (deleteConfirm) {
			//update the state
			const allCustomers = customers;
			const filtered = allCustomers.filter((m) => m._id !== id);
			setCustomers(filtered);
			try {
				await firestore.collection('customers').doc(id).delete();
			} catch (error) {
				toast.error('cant perform this operation');
				setCustomers(allCustomers);
			}

			//call the database to delete the customer with the id

			toast.success('deleted successfully');
			console.log(id);
		}
	};
	const handleEdit = (id: string) => {
		//find the customer with the give id and edit
		console.log(id);
	};
	useEffect(() => {
		async function getCustomers() {
			const citiesRef = firestore.collection('customers');
			const snapshot = await citiesRef.get();

			const customers: Array<Customers> = [];

			snapshot.forEach((doc) => {
				let currentId = doc.id;
				let appObj = { ...doc.data(), ['_id']: currentId };
				customers.push(appObj as Customers);
				console.log(customers);
			});

			setCustomers(customers);
		}
		getCustomers();
	}, []);
	return (
		<Card className="pa-2">
			<Card.Header>
				<span>Customers</span>
				<Accordion>
					<Accordion.Toggle className="float-right btn btn-primary m-2" eventKey="0">
						ADD
					</Accordion.Toggle>
					<Accordion.Collapse eventKey="0">
						<Card.Body>
							<InputGroup className="mb-3">
								<InputGroup.Prepend>
									<InputGroup.Text>Details</InputGroup.Text>
								</InputGroup.Prepend>
								<FormControl
									type="text"
									placeholder="Customers name.."
									value={name}
									onChange={(e) => setName(e.target.value)}
								/>
								<FormControl
									type="email"
									placeholder="Customers Email"
									value={email}
									onChange={(e) => setEmail(e.target.value)}
								/>
								<FormControl
									type="text"
									placeholder="Customers address"
									value={address}
									onChange={(e) => setAddress(e.target.value)}
								/>
								<FormControl
									type="tel"
									placeholder="phone Number"
									value={phone}
									onChange={(e) => setPhone(e.target.value)}
								/>
								<Button className="ml-2" onClick={AddCustomer}>
									{loading ? <Spinner animation="border" variant="ligth" /> : <span>Insert</span>}
								</Button>
							</InputGroup>
						</Card.Body>
					</Accordion.Collapse>
				</Accordion>
			</Card.Header>
			<Card.Body>
				<Table striped bordered hover>
					<thead>
						<tr>
							<th>
								<span className="material-icons">person</span>
							</th>
							<th>
								<span className="material-icons">email</span>
							</th>
							<th>
								<span className="material-icons">location</span>
							</th>
							<th>
								<span className="material-icons">contact_phone</span>
							</th>
							<th>
								<span className="material-icons">add</span>
							</th>
							<th>
								<span className="material-icons">delete</span>
							</th>
						</tr>
					</thead>
					<tbody>
						{customers.map((customer) => (
							<tr key={customer.id}>
								<td>{customer.name}</td>

								<td>{customer.email}</td>
								<td>{customer.address}</td>
								<td>{customer.phone}</td>

								<td>
									<Button onClick={() => handleEdit(customer._id)}>Edit</Button>
								</td>
								<td>
									<Button onClick={() => handleDelete(customer._id)} className="btn-danger">
										Delete
									</Button>
								</td>
							</tr>
						))}
					</tbody>
				</Table>
			</Card.Body>
			<ToastContainer></ToastContainer>
		</Card>
	);
}
