import React, { useState, useEffect, useContext } from 'react';
import { Accordion, Button, Card, Container, FormControl, InputGroup, Table } from 'react-bootstrap';
import useFirebase from '../components/useFirebase';
import firebase from 'firebase';
import { useRouter } from 'next/router';

interface Customers {
	id: string;
	name: string;
	email: string;
	address: string;
	phone: string;
}
[];
export default function Customers() {
	const [customers, setCustomers] = useState<Customers[]>([]);
	const [name, setName] = useState('');
	const [email, setEmail] = useState('');
	const [phone, setPhone] = useState('');
	const router = useRouter();
	const handleDelete = (id: any) => {
		console.log(id);
	};
	const handleEdit = (id: any) => {
		console.log(id);
	};
	useEffect(() => {
		const result = async () => {
			// await db.collection('customers').get()
		};
	});
	return (
		<Container>
			<Card className="pa-2">
				<Card.Header>
					<span className="material-icons">Customers_table</span>
					<Accordion>
						<Accordion.Toggle as={Button} variant="link" className="float-right" eventKey="0">
							<Button
								className="btn btn-info float-right"
								// onClick={() => {
								// 	router.push('/');
								// }}
							>
								NEW CUSTOMER
							</Button>
						</Accordion.Toggle>
						<Accordion.Collapse eventKey="0">
							<Card.Body>
								<InputGroup className="mb-3">
									<InputGroup.Prepend>
										<InputGroup.Text>Enter Customer details</InputGroup.Text>
									</InputGroup.Prepend>
									<FormControl type="text" placeholder="name.." />
									<FormControl type="email" placeholder="email" />
									<FormControl type="text" placeholder="address" />
									<FormControl type="text" placeholder="phone" />
									<Button className="pa-2">Edit</Button>
								</InputGroup>
							</Card.Body>
						</Accordion.Collapse>
					</Accordion>
				</Card.Header>
				<Card.Body>
					<Table striped bordered hover>
						<thead>
							<tr>
								<th>
									<span className="material-icons">Name</span>
								</th>
								<th>
									<span className="material-icons">Email</span>
								</th>
								<th>
									<span className="material-icons">Address</span>
								</th>
								<th>
									<span className="material-icons">Phone</span>
								</th>
								<th>
									<span className="material-icons">add</span>
								</th>
								<th>
									<span className="material-icons">delete</span>
								</th>
							</tr>
						</thead>
						<tbody>
							{customers.map((customer) => (
								<tr key={customer.id}>
									<td>{customer.name}</td>

									<td>{customer.email}</td>
									<td>{customer.address}</td>
									<td>{customer.phone}</td>
									<td>
										<Button onClick={() => handleEdit(customer.id)}>Edit</Button>
									</td>
									<td>
										<Button onClick={() => handleDelete(customer.id)}>Delete</Button>
									</td>
								</tr>
							))}
						</tbody>
					</Table>
				</Card.Body>
			</Card>
		</Container>
	);
}
