import React, { useState, useEffect, useContext } from 'react';
import { Button, Card, Container, Table } from 'react-bootstrap';
import useFirebase from '../components/useFirebase';
import firebase from 'firebase';

interface Customers {
	id: string;
	name: string;
	email: string;
	address: string;
	phone: string;
}
[];
export default function Customers() {
	const [customers, setCustomers] = useState<Customers[]>([]);
	const app = useContext(useFirebase);
	const db = app.firestore();

	const handleDelete = (id: any) => {
		console.log(id);
	};
	const handleEdit = (id: any) => {
		console.log(id);
	};
	useEffect(() => {});
	return (
		<Container>
			<Card>
				<Card.Header>
					Customers <span className="material-icons">Customers_table</span>
				</Card.Header>
				<Card.Body>
					<Table>
						<thead>
							<tr>
								<th>
									<span className="material-icons">Name</span>
								</th>
								<th>
									<span className="material-icons">Email</span>
								</th>
								<th>
									<span className="material-icons">Address</span>
								</th>
								<th>
									Phone <span className="material-icons">call</span>
								</th>
								<th>
									<span className="material-icons">add</span>
								</th>
								<th>
									<span className="material-icons">delete_forever</span>
								</th>
							</tr>
						</thead>
						<tbody>
							{customers.map((customer) => (
								<tr key={customer.id}>
									<th>{customer.name}</th>

									<th>{customer.email}</th>
									<th>{customer.address}</th>
									<th>{customer.phone}</th>
									<th>
										<Button onClick={() => handleEdit(customer.id)}>Edit</Button>
									</th>
									<th>
										<Button onClick={() => handleDelete(customer.id)}>Delete</Button>
									</th>
								</tr>
							))}
						</tbody>
					</Table>
				</Card.Body>
			</Card>
		</Container>
	);
}
